import React, { useState } from 'react';
import ToggleMeasure from '../ToggleMeasure';
import RenderIngredient from '../RenderIngredient';
import { CHANGE_SUBMEASURE } from '../../utils/actions';
import { useStoreContext } from "../../utils/GlobalState";

function ViewEditRecipe() {
    const [ state, dispatch ] = useStoreContext();
    const [addIngredientState, setAddIngredientState] = useState({ 
        //This is how data will be structured in each ingredient
        ingredient: '', amount: {
            'weight-metric':
            {
                value: '',
                submeasure: 'grams'
            },
            'weight-imperial':
            {
                value: '',
                submeasure: 'ounces'
            },
            'volume-metric':
            {
                value: '',
                submeasure: 'milliliters'
            },
            'volume-imperial':
            {
                value: '',
                submeasure: 'cups'
            }
        }
    });
    //recipe name and array of ingredients
    const [recipeState, setRecipeState] = useState({ 
        recipe: '', 
        ingredients: []
    });

    //'measure' tells us what measurement to display based on which options are selected
    const measure = `${state.measureType}-${state.measureSystem}`;

    // Push new ingredient to 'ingredients' array
    function addIngredient(event) {
        event.preventDefault();

        setRecipeState({
            //Add ingredient to array of objects in recipeState
            ...recipeState,
            ingredients: [...recipeState.ingredients, {
                ingredient: addIngredientState.ingredient,
                    amount: {
                        ...addIngredientState.amount
                    }  
            }]
        });

        // Reset ingredient input form to original state
        setAddIngredientState({
            ingredient: '',
            amount: {
                'weight-metric':
                {
                    value: '',
                    submeasure: 'grams'
                },
                'weight-imperial':
                {
                    value: '',
                    submeasure: 'ounces'
                },
                'volume-metric':
                {
                    value: '',
                    submeasure: 'milliliters'
                },
                'volume-imperial':
                {
                    value: '',
                    submeasure: 'cups'
                }
            }
        });
    };

    //Updates global
    function updateSubMeasure(submeasure) {
        dispatch({
            type: CHANGE_SUBMEASURE,
            subMeasure: submeasure
        });
    }

    // Each time text is entered to input, update formState
    const handleNameChange = event => {
        const { name, value } = event.target;
        setAddIngredientState({
          ...addIngredientState,
          ingredient: value
        });
    };

    const handleAmountChange = event => {
        const { value } = event.target;
        const submeasure = document.getElementById('measurementSelect');
        console.log(submeasure.children);

        //Turn 'amount' string to integer for math-y purposes
        const amountInt = parseInt(value);

        setAddIngredientState({
          ...addIngredientState,
          amount: {
              ...addIngredientState.amount,
              [measure]:
              {
                  value: amountInt,
                  submeasure: submeasure
              }
            }
        });
        console.log(addIngredientState);
    };

    return(
        <div>
            {/* Change measurement type here */}
            <ToggleMeasure/>

            {/* Map array of ingredients */}
            <div>
                    {recipeState.ingredients.map(ingredient => (
                        <RenderIngredient key={ingredient.ingredient} name={ingredient.ingredient} amount={ingredient.amount} measure={measure}/>
                    ))}
            </div>

            {/* Ingredient Input */}
                <form onSubmit={addIngredient}>
                    <div>
                        <input type="text" 
                            placeholder="Ingredient" 
                            id='ingredientNameInput'
                            name='ingredient'
                            value={addIngredientState.ingredient}
                            autoComplete="off" 
                            onChange={handleNameChange}/>

                        <input type="text" 
                            placeholder="Amount"
                            id='ingredientAmtInput'
                            name='amount'
                            value={addIngredientState.amount[measure].value}
                            onChange={handleAmountChange}
                            autoComplete="off"/>
                    </div>

                    {/* Measurement Select, Displays each set of options conditionally based on state */}

                    {/* if Imperial */}
                    <div id='measurementSelect'>
                        {(measure === 'volume-imperial') && (
                        <select className="imperialVolumeSelect" defaultValue='cups'>
                            <option value="gallons">G</option>
                            <option value="quarts">Q</option>
                            <option value="cups">C</option>
                            <option value="tablespoons">T</option>
                            <option value="teaspoons">t</option>
                            <option value="fluid ounces">fl. oz.</option>
                        </select>
                        )}

                        {(measure === 'weight-imperial') && (
                        <select className="imperialWeightSelect" defaultValue="ounces">
                            <option value="pounds">lbs.</option>
                            <option value="ounces">oz.</option>
                        </select>
                        )}
                        {/* if Metric */}
                        {(measure === 'volume-metric') && (
                        <select className="metricVolumeSelect" defaultValue="milliliters">
                            <option value="liters">L</option>
                            <option value="deciliters">dL</option>
                            <option value="milliliters">mL</option>
                        </select>
                        )}

                        {(measure === 'weight-metric') && (
                        <select className="metricWeightSelect" defaultValue="grams">
                            <option value="kilograms">kg</option>
                            <option value="grams">g</option>
                            <option value="milligrams">mg</option>
                        </select>
                        )}
                    </div> 
                    {/* Add Ingredient Button */}
                    <div>
                        <input type="submit" value="+"></input>
                    </div>
                </form>
        </div>
    )
};

export default ViewEditRecipe;
