export function convertSubMeasure(current, requested, measurement) {

    const measures = {
       grams: 1,
       milligrams: .001,
       kilograms: 100
    }

    if (measures[current] > measures[requested]) {
        const newMeasure = measures[current] * measures[requested] * measurement;
        return newMeasure;
    }

    else if (measures[current] < measures[requested]) {
        const newMeasure = measures[current] / measures[requested] * measurement;
        return newMeasure;
    }
}

export function convertMeasure(current, requested, measurement, submeasure) {
    const conversion = `${current}->${requested}`;

    switch(conversion) {
        case 'weight-metric->volume-metric':
            if (submeasure !== 'grams') {
                measurement = convertSubMeasure(submeasure, 'grams', measurement);
            }
            return {
                newMeasurement: measurement,
                newSubmeasure: 'milliliters'
            }
    }
}
