import React, { useState } from 'react';
import { useStoreContext } from "../../utils/GlobalState";
import { CHANGE_MEASURE_TYPE, CHANGE_MEASURE_SYSTEM } from '../../utils/actions';

function ToggleMeasure() {
    const [ state, dispatch ] = useStoreContext();

    function afterDispatch() {
        console.log(state.measureType, ' after dispatch');
    }

    function handleTypeChange(event) {
        event.preventDefault();

        const value = event.target.value;
        console.log(state.measureType, ' before dispatch');
        console.log(value, ' value'); 

        dispatch({
            type: CHANGE_MEASURE_TYPE,
            measureType: value
        });

        afterDispatch();
    }

    function handleSystemChange(event) {
        event.preventDefault();

        const value = event.target.value;

        dispatch({
            type: CHANGE_MEASURE_SYSTEM,
            measureSystem: value
        });    
    }

    return (
        <form> 
            {/* Measurement Options */}
            {/* system */}
            <select onChange={handleSystemChange}>
                <option value="metric">M</option>
                <option value="imperial">I</option>
            </select>
            {/* type */}
            <select onChange={handleTypeChange}>
                    <option value="weight">WEIGHT</option>
                    <option value="volume">VOLUME</option>
            </select>
            <label htmlFor="autoConvertToggle">autoconvert</label>
            <input type="checkbox" id="autoConvertToggle"></input>
        </form>
    )
}

export default ToggleMeasure;
