import React, { useState } from 'react';
import { useStoreContext } from "../../utils/GlobalState";
import { Recipe } from '../Recipe/Recipe';
import './index.css'

interface PropsInterface {
    setRecipe: Function,
    recipe: Recipe
}

const ToggleGlobals: React.FC<PropsInterface> = (props: PropsInterface) => {
    const [ state, dispatch ] = useStoreContext();
    const system = props.recipe.system;
    const units = props.recipe.units;

    const handleChange = (event: React.FormEvent): void =>  {
        const { value, id } = event.target as HTMLFormElement;
        console.log('change');

        if (id === 'system') console.log('sys'); props.setRecipe(props.recipe.changeState( value, units ));
        if (id === 'units') console.log('unit'); props.setRecipe(props.recipe.changeState( system, value ));
    }

    return (
        <form className='toggle-measure'> 
            {/* Measurement Options */}
            <div>
                {/* system */}
                <select id='system' onChange={handleChange}>
                    <option value="metric">M</option>
                    <option value="us">US</option>
                </select>
                {/* type */}
                <select id='units' onChange={handleChange}>
                        <option value="weight">WEIGHT</option>
                        <option value="volume">VOLUME</option>
                </select>
            </div>
            <div className='edit-percent'>
                <input type='number' defaultValue={state.recipeState.percent} placeholder='100'></input>
                <a> X</a>
            </div>
        </form>
    )
}

export default ToggleGlobals;
