export class Ingredient {
    constructor(data) {
        this.name = data.name;
        this.weight = {
            value: false,
            default: false
        }
        this.volume = {
            value: false,
            default: false
        }
        Object.assign(this, data);
    }

    default(state) {
        return this[state].default;
    }

    convert(unit) {
        switch (unit) {
            case 'grams': 
                return this.value / 1;
            case 'milligrams':
                return this.value / .001;
            case 'kilograms':
                return this.value / 1000;

            case 'milliliters':
                return this.value / 1;
            case 'liters':
                return this.value / 1000;

            case 'ounces':
                return this.value / 28;
            case 'pounds':
                return this.value / 454;

            case 'cups':
                return this.value / 240;
            case 'quarts':
                return this.value / 946;
            case 'gallons':
                return this.value / 3785;
            case 'teaspoons':
                return this.value / 29.575;
            case 'tablespoons':
                return this.value / 14.787;
            case 'fluidOunces':
                return this.value / 4.929;
        }
    }
}
