export function convertSubMeasure(current, requested, measurement) {
    const conversion = `${current}->${requested}`;
    const measurementInt = parseInt(measurement);

    switch(conversion) {
        case 'grams->milligrams':
            return toString(measurementInt*100);

        case 'grams->kilograms':
            return toString(measurementInt/1000);

        case 'milligrams->grams':
            return toString(measurementInt/100);

        case 'milligrams->kilograms':
            return toString(measurementInt/100000);

        case 'kilograms->milligrams':
            return toString(measurementInt*100000);   

        case 'kilograms->grams':
            return toString(measurementInt*1000);

    }
}
