import React, { useState } from 'react';
import ToggleMeasure from '../ToggleMeasure';
import { useStoreContext } from "../../utils/GlobalState";

function ViewEditRecipe() {
    const [ state ] = useStoreContext();
    const [addIngredientState, setAddIngredientState] = useState({ 
        ingredient: '', amount: ''
    });

    const ingredients = [];
    const measure = `${state.measureType}-${state.measureSystem}`

    function addIngredient(event) {
        event.preventDefault();
        addIngredientState.ingredient = `${addIngredientState.ingredient}`

        ingredients.push({
            ingredient: addIngredientState.ingredient,
            amount: {
                [measure]: addIngredientState.amount
            }
        });
    }

    // Each time text is entered to input, update formState
    const handleChange = event => {
        const { name, value } = event.target;
        setAddIngredientState({
          ...addIngredientState,
          [name]: value
        });
    }

    return(
        <div>
            {/* Change measurement type here */}
            <ToggleMeasure/>

            {/* Map array of ingredients */}
            <div>
                    {ingredients.map(ingredient => {

                    })}
            </div>

            {/* Ingredient Input */}
            <form onSubmit={addIngredient}>
                <div>
                    <input type="text" 
                        placeholder="Ingredient" 
                        id='ingredientNameInput'
                        name='ingredient'
                        onChange={handleChange}></input>
                    <input type="text" 
                        placeholder="Amt."
                        id='ingredientAmtInput'
                        name='amount'
                        onChange={handleChange} 
                        className="ingredientAmtInput"></input>

                    {/* Conditional Measurement Select */}
                    {/* if Imperial */}
                    <div>
                        {(measure === 'volume-imperial') && (
                        <select className="imperialVolumeSelect">
                            <option value="gallons">G</option>
                            <option value="quarts">Q</option>
                            <option value="cups">C</option>
                            <option value="tablespoons">T</option>
                            <option value="teaspoons">t</option>
                            <option value="fluid ounces">fl. oz.</option>
                        </select>
                        )}

                        {(measure === 'weight-imperial') && (
                        <select className="imperialWeightSelect">
                            <option value="pounds">lbs.</option>
                            <option value="ounces">oz.</option>
                        </select>
                        )}
                        {/* if Metric */}
                        {(measure === 'volume-metric') && (
                        <select className="metricVolumeSelect">
                            <option value="liters">L</option>
                            <option value="deciliters">dL</option>
                            <option value="milliliters">mL</option>
                        </select>
                        )}

                        {(measure === 'weight-imperial') && (
                        <select className="metricWeightSelect">
                            <option value="kilograms">kg</option>
                            <option value="grams">g</option>
                            <option value="milligrams">mg</option>
                        </select>
                        )}
                    </div> 
                </div>
                {/* Add Ingredient Button */}
                <div>
                    <input type="submit" value="+"></input>
                </div>
            </form>
        </div>
    )
}

export default ViewEditRecipe;
