import React, { useState } from "react";
import { useStoreContext } from "../../utils/GlobalState";
import { convertSubMeasure } from "../../utils/helpers"


function RenderIngredient({ name, amount, measure }) {
    const [ state ] = useStoreContext();

    const [ingredientSubmeasureState, setIngredientSubmeasureState] = useState({
        measure: measure,
        submeasure: amount[measure].submeasure,
        newMeasurement: false
    })

    const ingredientMeasurement = getIngredientMeasurement();

    function getIngredientMeasurement() {
        //If global state measurement has been changed, set submeasure to no "newMeasurement" and return value or false
        if (ingredientSubmeasureState.measure !== measure) {
            setIngredientSubmeasureState({
                measure: measure,
                submeasure: amount[measure].submeasure,
                newMeasurement: false
            });

            return(amount[measure].value);
        } 

        else if (ingredientSubmeasureState.newMeasurement) {
            return(ingredientSubmeasureState.newMeasurement);
        } 

        else if (amount[measure].value){
            return(amount[measure].value);
        }

        else {
            return(false);
        }
    }

    function handleSubMeasureChange(event) {
        const newSubmeasure = event.target.value;
        const newMeasurement = convertSubMeasure(ingredientSubmeasureState.submeasure, newSubmeasure, ingredientMeasurement).toString();
        setIngredientSubmeasureState({
            measure: measure,
            submeasure: newSubmeasure,
            newMeasurement: newMeasurement 
        }) 
    }

    return(
        <div>
            <div>{name}</div>

            <div>
                { ingredientMeasurement 
                    ? (<div>{ingredientMeasurement}</div>)
                    : (<form>
                        <input type="text" placeholder="No Amt."/>
                    </form>)
                }        
            </div>



            <form id={`${name}MeasurementSelect`} onChange={handleSubMeasureChange}>
                        {(measure === 'volume-imperial') && (
                        <select className="imperialVolumeSelect" defaultValue={amount[measure].submeasure}>
                            <option value="gallons">G</option>
                            <option value="quarts">Q</option>
                            <option value="cups">C</option>
                            <option value="tablespoons">T</option>
                            <option value="teaspoons">t</option>
                            <option value="fluid ounces">fl. oz.</option>
                        </select>
                        )}

                        {(measure === 'weight-imperial') && (
                        <select className="imperialWeightSelect" defaultValue={amount[measure].submeasure}>
                            <option value="pounds">lbs.</option>
                            <option value="ounces">oz.</option>
                        </select>
                        )}
                        {/* if Metric */}
                        {(measure === 'volume-metric') && (
                        <select className="metricVolumeSelect" defaultValue={amount[measure].submeasure}>
                            <option value="liters">L</option>
                            <option value="deciliters">dL</option>
                            <option value="milliliters">mL</option>
                        </select>
                        )}

                        {(measure === 'weight-metric') && (
                        <select className="metricWeightSelect" defaultValue={amount[measure].submeasure}>
                            <option value="kilograms">kg</option>
                            <option value="grams">g</option>
                            <option value="milligrams">mg</option>
                        </select>
                        )}
                    </form>    
        </div>
    )
}

export default RenderIngredient;
