import React, { useState } from "react";
import { useStoreContext } from "../../utils/GlobalState";

function RenderIngredient({ name, amount }) {
    const [ state, dispatch ] = useStoreContext();

    const measure = `${state.measureType}-${state.measureSystem}`;

    return(
        <div>
            <div>{name}</div>
            {amount[measure] ? (<div>{amount[measure]}</div>):(<div></div>)}
        </div>
    )
}

export default RenderIngredient;
