import { relativeValues } from "./relativeValues";

export class Ingredient {
    constructor(data, system, unit) {
        this.system = system;
        this.unit = unit;
        this.weight = {
            value: false,
            default: false
        }
        this.volume = {
            value: false,
            default: false
        }
        Object.assign(this, data);
    }

    default() {
        return this.convert(this[this.unit].default);
    }

    convert(unit, state) {
        const relativeValue = relativeValues[this.system][this.unit][unit].value;
        return this[this.unit].value / relativeValue;
    }
}
