export function convertMeasure([current], [requested], measurement) {    
    const log = (...args) => console.log(...args);

    const measures = {
        weight_metric: {
            grams: 1,
            milligrams: .001,
            kilograms: 100
        },

        metric_volume: {
            milliliters: 1,
            liters: 1000
        },

        us_volume: {
            cups: 240,
            quarts: 946,
            gallons: 3785,
            fluid_ounces: 29.574,
            tablespoons: 14.787,
            teaspoon: 4.929
        }

    }

    if (!requested) {
        const newPossibleRequested = {};
        Object.values(measures[newSystem]).forEach(val => {
            const difference = measures[system][current] - val
            log(difference);
        });
    }

    const newMeasurement = measures[current] / measures[system][requested] * measurement

    return { 
        newMeasurement: newMeasurement,
        newSubmeasure: requested
    }

}
