import React from "react";

function DisplayMeasurement({ changedMeasure, defaultMeasure }) {
        if (changedMeasure) {
            return (<div>{changedMeasure}</div>);
        }

        else if (defaultMeasure) {
            return (<div>{defaultMeasure}</div>);
        }

        else {
            return (
                <form>
                    <input type="text" placeholder="No Amt."/>
                </form>
            );
        }
}

export default DisplayMeasurement;
