export class Ingredient {
    constructor(measurement) {
        this.weight = {
            value: false,
            default: false
        }
        this.volume = {
            value: false,
            default: false
        }
        Object.assign(this, measurement);
    }

    convert(unit) {
        switch (unit) {
            case grams: 
            case milligrams:
                return 'happy';
            case kilograms:

            case milliliters:
            case liters:

            case ounces:
            case pounds:

            case cups:
            case quarts:
            case gallons:
            case teaspoons:
            case tablespoons:
            case fluidOunces:
        }
    }
}
