export function convertSubMeasure(current, requested, measurement) {
    const conversion = `${current}->${requested}`;

    switch(conversion) {
        //metric weight
        case 'grams->milligrams':
            return measurement*100;

        case 'grams->kilograms':
            return measurement*.001;

        case 'milligrams->grams':
            return measurement*.01;

        case 'milligrams->kilograms':
            return measurement*.00001;

        case 'kilograms->milligrams':
            return measurement*100000;   

        case 'kilograms->grams':
            return measurement*1000;

        //metric volume
        case 'liters->deciliters':
            return measurement*10;

        case 'liters->milliliters':
            return measurement*1000;

        case 'deciliters->liters':
            return measurement*.1;

        case 'deciliters->milliliters':
            return measurement*100;

        case 'milliliters->liters':
            return measurement*.001;

        case 'milliliters->deciliters':
            return measurement*.01;

        //imperial weight
        case 'pounds->ounces':
            return measurement*16;

        case 'ounces->pounds':
            return measurement/16;

        //imperial volume 
        case 'fluid-ounces->teaspoons':
            return measurement*.166667;

        case 'fluid-ounces->tablespoons':
            return measurement*2;

        case 'fluid-ounces->cups':
            return measurement*8.115;

        case 'fluid-ounces->quarts':
            return measurement*32;

        case 'fluid-ounces->gallons':
            return measurement*128;
    }
}

