import { relativeValues } from "./relativeValues";

export class Ingredient {
    constructor(data, unit, system) {
        this.system = system;
        this.unit = unit;
        this.weight = {
            value: false,
            default: false
        }
        this.volume = {
            value: false,
            default: false
        }
        Object.assign(this, data);
    }

    default() {
        const { unit, system } = this;
        return this.convert(this[unit].default);
    }

    convert(requested) {
        const { unit, system } = this;
        const relativeValue = relativeValues[system][unit][requested].value;
        return this[this.unit].value / relativeValue;
    }
}
