export const relativeMeasurements = {
    metric: {
        weight: {
            milligrams: .001,
            grams: 1,
            kilograms: 1000
        },
        volume: {
            milliliters: 1,
            liters: 1000
        }
    },
    us: {
        weight: {
            pounds: 454,
            ounces: 28
        },
        volume: {
            cups: 240,
            quarts: 946,
            gallons: 3785,
            teaspoons: 4.929,
            tablespoons: 14.787,
            fluidOunces: 29.575
        }
    }
}

export class Ingredient {
    constructor(data) {
        this.name = data.name;
        this.weight = {
            value: false,
            default: false
        }
        this.volume = {
            value: false,
            default: false
        }
        Object.assign(this, data);
    }

    default(state) {
        return this.convert(this[state.type].default);
    }

    convert(unit, state) {
        const relativeValue = relativeMeasurements[state.unit][state.type][unit];
        return this[state].value / relativeValue;
    }
}
