import { useReducer } from 'react';

import {
    CHANGE_MEASURE_SYSTEM,
    CHANGE_MEASURE_TYPE,
    CHANGE_SUBMEASURE 
} from './actions'

export const reducer = (state, action) => {
    switch (action.type) {
        case CHANGE_MEASURE_TYPE:
            return {
                ...state,
                measureType: action.measureType,
                subMeasure: action.subMeasure
            }
        case CHANGE_MEASURE_SYSTEM:
            return {
                ...state,
                measureSystem: action.measureSystem,
                subMeasure: action.subMeasure
            }
        case CHANGE_SUBMEASURE:
            return {
                ...state,
                subMeasure: action.subMeasure
            }
    }
}

export function useIngredientReducer(initialState) {
	return useReducer(reducer, initialState);
}
