export class Ingredient {
    constructor(measurement) {
        this.weight = {
            value: false,
            default: false
        }
        this.volume = {
            value: false,
            default: false
        }
        Object.assign(this, measurement);
    }

    default(request) {
        return this[request][request.default]();
    };

    grams() {};
    milligrams() {};
    kilograms() {};

    milliliters() {};
    liters() {};

    ounces() {};
    pounds() {};

    cups() {};
    quarts() {};
    gallons() {};
    teaspoons() {};
    tablespoons() {};
    fluidOunces() {};
}
