import React, { useState } from "react";
import { useStoreContext } from "../../utils/GlobalState";
import { convertSubMeasure } from "../../utils/helpers"

function RenderIngredient({ name, amount, measure }) {
    const [ state, dispatch ] = useStoreContext();

    const [ingredientSubmeasureState, setIngredientSubmeasureState] = useState({
        submeasure: amount.submeasure,
        newMeasurement: false
    })

    const measurement = amount[measure];


    function handleSubMeasureChange(event) {
        const newSubMeasure = event.target.value;
        const newMeasurement = convertSubMeasure(ingredientSubmeasureState.submeasure, newSubMeasure, measurement);

        setIngredientSubmeasureState({
            submeasure: newSubMeasure,
            newMeasurement: newMeasurement
        }) 
        console.log(newMeasurement, ingredientSubmeasureState);
    }

    function displayMeasurement() {
        if (ingredientSubmeasureState.newMeasurement) {
            return (<div>{ingredientSubmeasureState.newMeasurement}</div>);
        }

        else if (measurement) {
            return (<div>{measurement}</div>);
        }

        else {
            return (
                <form>
                    <input type="text" placeholder="No Amt."/>
                </form>
            );
        }
    }

    return(
        <div>
            <div>{name}</div>
            <div>{displayMeasurement}</div>
            <form id='measurementSelect' onChange={handleSubMeasureChange}>
                        {(measure === 'volume-imperial') && (
                        <select className="imperialVolumeSelect" defaultValue={ingredientSubmeasureState.submeasure}>
                            <option value="gallons">G</option>
                            <option value="quarts">Q</option>
                            <option value="cups">C</option>
                            <option value="tablespoons">T</option>
                            <option value="teaspoons">t</option>
                            <option value="fluid ounces">fl. oz.</option>
                        </select>
                        )}

                        {(measure === 'weight-imperial') && (
                        <select className="imperialWeightSelect" defaultValue={ingredientSubmeasureState.submeasure}>
                            <option value="pounds">lbs.</option>
                            <option value="ounces">oz.</option>
                        </select>
                        )}
                        {/* if Metric */}
                        {(measure === 'volume-metric') && (
                        <select className="metricVolumeSelect" defaultValue={ingredientSubmeasureState.submeasure}>
                            <option value="liters">L</option>
                            <option value="deciliters">dL</option>
                            <option value="milliliters">mL</option>
                        </select>
                        )}

                        {(measure === 'weight-metric') && (
                        <select className="metricWeightSelect" defaultValue={ingredientSubmeasureState.submeasure}>
                            <option value="kilograms">kg</option>
                            <option value="grams">g</option>
                            <option value="milligrams">mg</option>
                        </select>
                        )}
                    </form>    
        </div>
    )
}

export default RenderIngredient;
