import { observer } from "mobx-react-lite";
import React from "react";
import { Form } from "react-bootstrap";
import { Ingredient } from "../Recipe/Ingredient";
import { Recipe } from '../Recipe/Recipe'

interface IUnitSelect {
    recipe: Recipe
}

const UnitSelect = observer(({ recipe }: IUnitSelect) => {

    const { state } = recipe;

    const selectedUnit = recipe.data.returnSelected(state).unit;
    const currentUnits = recipe.data.returnCurrentUnits(state);

    const handleChangeUnit = (event: React.FormEvent): void => {
        const { value } = event.target as HTMLFormElement;

        recipe.data.updateSelected(state, value);
    }

    return (
        <Form.Select name='unit' defaultValue={selectedUnit} onChange={handleChangeUnit}>
            {currentUnits.map(unit => (
                <option key={unit.unit} value={unit.unit}>
                        {unit.abbrev}
                </option>
            ))}

        </Form.Select>
    );
});

export default UnitSelect;
