import React, { useEffect } from "react";
import { Form } from "react-bootstrap";
import { IngredientDataInterface, Ingredient } from "../Recipe/Ingredient";
import { State } from '../Recipe/Recipe'

interface UnitSelectInterface {
    data: Ingredient;
    state: State;
    formState?: IngredientDataInterface;
    setFormState?: Function;
}

const UnitSelect = ({ data, state, formState ,setFormState }: UnitSelectInterface) => {
    const selectedUnit = data.returnSelected(state).unit;
    const currentUnits = data.returnCurrentUnits(state);

    const handleChangeUnit = (event: React.FormEvent): void => {
        const { value } = event.target as HTMLFormElement;

        data.updateSelected(state, value);

        if (setFormState) setFormState({
            ...formState,
            unit: selectedUnit
        });
    }

    useEffect(() => {
        if (setFormState) setFormState({
            ...formState,
            unit: selectedUnit
        });
    }, [data]);

    return (
        <Form.Select name='unit' defaultValue={selectedUnit} onChange={handleChangeUnit}>
            {currentUnits.map(unit => (
                <option key={unit.unit} value={unit.unit}>
                        {unit.abbrev}
                </option>
            ))}

        </Form.Select>
    );
}

export default UnitSelect;
