export const relativeValues = {
    metric: {
        weight: {
            milligrams: { value: .001, abbrev: 'mg' },
            grams: { value: 1, abbrev: 'g' },
            kilograms:{ value: 1000, abbrev: 'k' } 
        },
        volume: {
            milliliters: { value: 1, abbrev: 'mL' },
            liters: { value: 1000, abbrev: 'L' }
        }
    },
    us: {
        weight: {
            pounds: { value: 454, abbrev: 'lbs.' },
            ounces: { value: 28, abbrev: 'oz.' }
        },
        volume: {
            cups: { value: 240, abbrev: 'C' },
            quarts: { value: 946, abbrev: 'Q' },
            gallons: { value: 3785, abbrev: 'G' },
            teaspoons: { value: 4.929, abbrev: 'tsp.' },
            tablespoons: { value: 14.787, abbrev: 'tbsp.' },
            fluidOunces: { value: 29.575, abbrev: 'fl. oz.' }
        }
    }
}

export class Ingredient {
    constructor(data, system, unit) {
        this.name = data.name;
        this.state = { system: system, unit: unit };
        this.weight = {
            value: false,
            default: false
        }
        this.volume = {
            value: false,
            default: false
        }
        Object.assign(this, data);
    }

    default(state) {
        return this.convert(this[state.unit].default);
    }

    convert(unit, state) {
        const relativeValue = relativeValues[state.unit][state.system][unit].value;
        return this[state.unit].value / relativeValue;
    }
}
